import pygame, base64, io
from pygame.locals import *

# Define some colors
black = (0, 0, 0)
white = (255, 255, 255)
green = (0, 255, 0)
red = (255, 0, 0)
blue = (0, 0, 255)

pygame.init()

# Set the width and height of the screen [width, height]
size = (700, 500)
width = size[0]
height = size[1]
screen = pygame.display.set_mode(size)

pygame.display.set_caption("Our Game")

# background
background = pygame.image.load(io.BytesIO(base64.b64decode(b''))).convert()

# player walking images
walk1 = pygame.image.load(io.BytesIO(base64.b64decode(b''))).convert()
walk2 = pygame.image.load(io.BytesIO(base64.b64decode(b''))).convert()
walk3 = pygame.image.load(io.BytesIO(base64.b64decode(b''))).convert()
walk4 = pygame.image.load(io.BytesIO(base64.b64decode(b''))).convert()
walk5 = pygame.image.load(io.BytesIO(base64.b64decode(b''))).convert()
walk6 = pygame.image.load(io.BytesIO(base64.b64decode(b''))).convert()

# player idle images
idle1 = pygame.image.load(io.BytesIO(base64.b64decode(b''))).convert()
idle2 = pygame.image.load(io.BytesIO(base64.b64decode(b''))).convert()
idle3 = pygame.image.load(io.BytesIO(base64.b64decode(b''))).convert()
idle4 = pygame.image.load(io.BytesIO(base64.b64decode(b''))).convert()

#player jump images
jump1 = pygame.image.load(io.BytesIO(base64.b64decode(b''))).convert()
jump2 = pygame.image.load(io.BytesIO(base64.b64decode(b''))).convert()
jump3 = pygame.image.load(io.BytesIO(base64.b64decode(b''))).convert()
jump4 = pygame.image.load(io.BytesIO(base64.b64decode(b''))).convert()
jump5 = pygame.image.load(io.BytesIO(base64.b64decode(b''))).convert()
jump6 = pygame.image.load(io.BytesIO(base64.b64decode(b''))).convert()
jump7 = pygame.image.load(io.BytesIO(base64.b64decode(b''))).convert()
jump8 = pygame.image.load(io.BytesIO(base64.b64decode(b''))).convert()

# Loop until the user clicks the close button.
done = False

# Used to manage how fast the screen updates
clock = pygame.time.Clock()
all_sprites = pygame.sprite.Group()

# Player
(x, y) = (width/4, height/1.5) # start player in left bottom 3rd of the screen
speed = 2
diag = 1
up = False
down = False
left = False
right = False
walk = 0
walk_count = 0
idle = 1
idle_count = 0
last_dir = "right"
jump = 0
jump_count = 0

class Player(pygame.sprite.Sprite):
    def __init__(self):
        pygame.sprite.Sprite.__init__(self)
        self.image = walk1
        self.rect = self.image.get_rect()
        self.rect.center = (x, y)
    def update(self):
        # left
        if left == True:
            self.rect.x -= speed
        if self.rect.right < 0:
            self.rect.left = width
        # right
        if right == True:
            self.rect.x += speed
        if self.rect.left > width:
            self.rect.right = 0
        # down
        if down == True:
            #self.rect.y += speed
            pass
        if self.rect.top > height:
            #self.rect.bottom = 0
            pass

        # animation right
        if last_dir in "right":
            if walk:
                if 0<=walk_count<5:
                    self.image = walk1
                if 5<walk_count<10:
                    self.image = walk2
                if 10<walk_count<15:
                    self.image = walk3
                if 15<walk_count<20:
                    self.image = walk4
                if 20<walk_count<25:
                    self.image = walk5
                if 25<walk_count<30:
                    self.image = walk6

            if jump:
                if 0<=jump_count<10:
                    self.image = jump1
                    self.rect.y -= speed
                if 10<jump_count<20:
                    self.image = jump2
                    self.rect.y -= speed
                if 20<jump_count<30:
                    self.image = jump3
                    self.rect.y -= speed
                if 30<jump_count<40:
                    self.image = jump4
                    self.rect.y -= speed
                if 40<jump_count<50:
                    self.image = jump5
                    self.rect.y += speed
                if 50<jump_count<60:
                    self.image = jump6
                    self.rect.y += speed
                if 60<jump_count<70:
                    self.image = jump7
                    self.rect.y += speed
                if 70<jump_count<80:
                    self.image = jump8
                    self.rect.y += speed

            if idle:
                if 0<=idle_count<10:
                    self.image = idle1
                if 10<idle_count<20:
                    self.image = idle2
                if 20<idle_count<30:
                    self.image = idle3
                if 30<idle_count<40:
                    self.image = idle4
                if 40<idle_count<50:
                    self.image = idle4
                if 50<idle_count<60:
                    self.image = idle3
                if 60<idle_count<70:
                    self.image = idle2
                if 70<idle_count<80:
                    self.image = idle1
        # animation left
        if last_dir in "left":
            if walk:
                if 0<=walk_count<5:
                    self.image = pygame.transform.flip(walk1, True, False)
                if 5<walk_count<10:
                    self.image = pygame.transform.flip(walk2, True, False)
                if 10<walk_count<15:
                    self.image = pygame.transform.flip(walk3, True, False)
                if 15<walk_count<20:
                    self.image = pygame.transform.flip(walk4, True, False)
                if 20<walk_count<25:
                    self.image = pygame.transform.flip(walk5, True, False)
                if 25<walk_count<30:
                    self.image = pygame.transform.flip(walk6, True, False)

            if jump:
                if 0<=jump_count<10:
                    self.image =  pygame.transform.flip(jump1, True, False)
                    self.rect.y -= speed
                if 10<jump_count<20:
                    self.image =  pygame.transform.flip(jump2, True, False)
                    self.rect.y -= speed
                if 20<jump_count<30:
                    self.image =  pygame.transform.flip(jump3, True, False)
                    self.rect.y -= speed
                if 30<jump_count<40:
                    self.image =  pygame.transform.flip(jump4, True, False)
                    self.rect.y -= speed
                if 40<jump_count<50:
                    self.image =  pygame.transform.flip(jump5, True, False)
                    self.rect.y += speed
                if 50<jump_count<60:
                    self.image =  pygame.transform.flip(jump6, True, False)
                    self.rect.y += speed
                if 60<jump_count<70:
                    self.image =  pygame.transform.flip(jump7, True, False)
                    self.rect.y += speed
                if 70<jump_count<80:
                    self.image =  pygame.transform.flip(jump8, True, False)
                    self.rect.y += speed

            if idle:
                if 0<=idle_count<10:
                    self.image = pygame.transform.flip(idle1, True, False)
                if 10<idle_count<20:
                    self.image = pygame.transform.flip(idle2, True, False)
                if 20<idle_count<30:
                    self.image = pygame.transform.flip(idle3, True, False)
                if 30<idle_count<40:
                    self.image = pygame.transform.flip(idle4, True, False)
                if 40<idle_count<50:
                    self.image = pygame.transform.flip(idle4, True, False)
                if 50<idle_count<60:
                    self.image = pygame.transform.flip(idle3, True, False)
                if 60<idle_count<70:
                    self.image = pygame.transform.flip(idle2, True, False)
                if 70<idle_count<80:
                    self.image = pygame.transform.flip(idle1, True, False)



player = Player()
all_sprites.add(player)

# -------- Main Program Loop -----------
while not done:
    # --- Main event loop
    for event in pygame.event.get():
        if event.type == pygame.QUIT:
            done = True

    # reset walk_count when not moving
    if not left and not right:
        walk = 0
        walk_count = 0
        idle = 1
        idle_count +=1
    # reset to Loop walk animation
    if walk_count >= 30:
        walk_count = 0
    # reset to loop idle animation
    if idle_count >= 80:
        idle_count = 0
    #stop jump animation
    if jump_count >= 80:
        jump = 0
        jump_count = 0

    # keypresses
    keys = pygame.key.get_pressed()
    # left
    if keys[K_LEFT]:
        left = True
        walk_count += 1
        idle = 0
        idle_count = 0
        last_dir = "left"
        walk = 1
    elif not keys[K_LEFT]:
        left = False
    # right
    if keys[K_RIGHT]:
        right = True
        walk_count += 1
        idle = 0
        idle_count = 0
        last_dir = "right"
        walk = 1
    elif not keys[K_RIGHT]:
        right = False
    # up
    if keys[K_UP]:
        up = True
        jump = 1
    elif not keys[K_UP]:
        up = False
    # down
    if keys[K_DOWN]:
        down = True
    elif not keys[K_DOWN]:
        down = False
    if jump:
        idle = 0
        idle_count = 0
        walk = 0
        walk_count = 0
        jump_count += 1
    else:
        pass



    # --- Screen-clearing code goes here

    # Here, we clear the screen to white. Don't put other drawing commands
    # above this, or they will be erased with this command.

    # If you want a background image, replace this clear with blit'ing the
    # background image.
    all_sprites.update()
    screen.blit(background)


    # --- Drawing code should go here
    all_sprites.draw(screen)
    # --- Go ahead and update the screen with what we've drawn.
    pygame.display.update()

    # --- Limit to 60 frames per second
    clock.tick(60)

# Close the window and quit.
pygame.quit()
